mapper.select:
select count(*) from members

	where id like #{keyword}
		

	where name like #{keyword}

select * from members		

	where id like #{keyword}
		

	where name like #{keyword}
	
order by id desc

mapper.update:
update members set
name=#{name}, tel=#{tel}, hp=#{hp}, nick=#{nick}, email=#{email}, postcode1=#{postcode1}, address1=#{address1}, detailaddress1=#{detailaddress1}, 
postcode2=#{postcode2}, address2=#{address2}, detailaddress2=#{detailaddress2}
where id=#{id}

update members set
point = point + #{point} 
where id=#{id}
